import React from 'react';
import {Card} from 'semantic-ui-react'

const PetCard = (props) => {


    return (

        <div style={{ justifyContent:'center', alignItems:'center', height: '50em'}} className="ui card four wide column" onClick={() => props.setSelectedPet(props.pet)}>
          <div className="image">
            <img style={{objectFit: "cover", height: "35em"}} alt="" src={props.pet.img_url}/>
          </div>
          <div className="content">
            <h1>Name: {props.pet.name}</h1>
            <h1>Age: {props.pet.age} Months</h1>
          </div>
        </div>
    )
}

export default PetCard
