import React from 'react';
import './App.css';

class App extends React.Component {

  state = {
    pets: []
  }

  fetchPets = () => {
    fetch("http://localhost:4000/api/v1/pets")
    .then(res => res.json())
    .then(pets => {
      this.setState({
        pets: pets
      })
    })
  }

  componentDidMount(){
    this.fetchPets()
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <p>
            Hello, welcome to PawsUp!
          </p>
          <img src="https://i.redd.it/vuhwjpc8hb821.jpg"/>
        </header>
      </div>
    );
  }
}

export default App;
