import React from 'react';




class Filter extends React.Component {



  removeElement(array, element){
    let filterArray = array.filter((member)=>{
    return  member !== element
    })
    return filterArray
  }

  handleAnimalChange = (e) => {
    if(e.target.value) {
    this.props.setAnimalFilter(e.target.value)
    }
    else {
    this.props.setAnimalFilter(null)
    }
  }

  handleSizeChange = (e) => {
    if(e.target.value) {
    this.props.setSizeFilter(e.target.value)
    }
    else {
    this.props.setSizeFilter(null)
    }
  }

  handleSexChange = (e) => {
    if(e.target.value) {
    this.props.setSexFilter(e.target.value)
    }
    else {
    this.props.setSexFilter(null)
    }
  }



  render(){
    return(
      <div>
        <div className="ui selection dropdown" onChange={this.handleAnimalChange}>
        <input type="hidden" name="animal"/>
        <i className="dropdown icon"></i>
        <div className="default text">Animal</div>
          <div className="menu">
            <div label="select"></div>
            <div className="item" dataValue="dog">Dog</div>
            <div className="item" dataValue="cat">Cat</div>
            <div className="item" dataValue="lizard">Lizard</div>
            <div className="item" dataValue="pig">Pig</div>
            <div className="item" dataValue="hamster">Hamster</div>
          </div>
        </div>
        Size<select onChange={this.handleSizeChange}>
        <option label="select"></option>
        <option value="Toy">Toy</option>
        <option value="Medium">Medium</option>
        <option value="Large">Large</option>
        </select>
        Sex<select onChange={this.handleSexChange}>
        <option label="select"></option>
        <option value="male">Male</option>
        <option value="female">Female</option>
        </select>
      </div>
    )
  }
}


export default Filter
