import React from 'react';

class Profile extends React.Component{

    renderAdopterProfile = () => {
      let adopterInfo = this.props.currentUser.map((adopter)=> {
        return (<div><h1>{adopter.name}</h1>
              <h1>{adopter.location}</h1></div>)
      })
      return adopterInfo
    }

    render(){
      console.log(this.props.currentUser)
      return(

        <div>{this.renderAdopterProfile()}</div>
      )
    }

}


export default Profile
