import React from 'react';
import './App.css';

class App extends React.Component {

  state = {
    pets: []
  }

  fetchPets = () => {
    fetch("http://localhost:4000/api/v1/pets")
    .then(res => res.json())
    .then(pets => {
      console.log(pets)
    })
  }

  componentDidMount(){
    this.fetchPets()

    fetch('http://localhost:4000/api/v1/meetings', {
    method: 'post',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      adopter_id: 2,
      pet_id: 3,
      location: "Boston",
      datetime: "2019-05-02T18:33:07.189Z"
    })
  })
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <p>
            Hello, welcome to PawsUp!
          </p>
          <img src="https://i.redd.it/vuhwjpc8hb821.jpg"/>
        </header>
      </div>
    );
  }
}

export default App;
