import React, {Fragment} from 'react';
import NavBar from '../components/NavBar.js'
import PetContainer from './PetContainer.js'
import PetSpec from './PetSpec.js'

class PetPage extends React.Component {

  state = {
    pets: []
  }

  fetchPets = () => {
    fetch("http://localhost:4000/api/v1/pets")
    .then(res => res.json())
    .then(pets => {
      this.setState({
        pets: pets
      })
    })
  }


  componentDidMount(){
    this.fetchPets()
  }


  render(){

  return (

    <Fragment>
    <h1>PawsUp<i src="https://previews.123rf.com/images/lynxtime/lynxtime1511/lynxtime151102961/48591597-dog-paw-print-icon.jpg"></i></h1>
    <NavBar />
    <PetContainer pets = {this.state.pets}/>
    <PetSpec />
    </Fragment>
  )
  }
}

export default PetPage
