import React from 'react';

class Profile extends React.Component{

    renderAdopterProfile = () => {
      if (this.props.currentUser){
        return (<div style={{position:"relative", right: "22em"}}>
    <img className="ui medium circular image" src={this.props.currentUser.img_url}/>
    <h1>{this.props.currentUser.name}</h1><div className="ui list">
  <div className="item">
    <i className="users icon"></i>
    <div className="content">
      Semantic UI
    </div>
  </div>
  <div className="item">
    <i className="marker icon"></i>
    <div className="content">
      {this.props.currentUser.location}
    </div>
  </div>
  <div className="item">
    <i className="birthday cake icon"></i>
    <div className="content">
      {this.props.currentUser.age} Years Old
    </div>
  </div>
  <div className="item">
    <i class="certificate icon"></i>
    <div className="content">
      {this.props.currentUser.allergy? <h1>Adopter Has Allergies</h1>: <h2>Adopter Has No Allergies</h2>}
    </div>
  </div>
</div>
  </div>)
      }
      else {
       return null
      }
    }

    render(){
      return(

        <div>{this.renderAdopterProfile()}</div>
      )
    }

}


export default Profile
