  handleChange = (e) => {
      if(e.target.checked){
      this.props.setAnimalFilterArray(this.state.animalArray)
    }
    else{
      this.props.setAnimalFilterArray("")
    }
  }


  render(){
    return(
      <div>
        Animal<form onChange={this.handleChange}>

            Dog <input value="dog" type="checkbox"/>
