import React from 'react';
import './App.css';
import PetContainer from './containers.PetContainer.js'

class App extends React.Component {

  state = {
    pets: []
  }

  fetchPets = () => {
    fetch("http://localhost:4000/api/v1/pets")
    .then(res => res.json())
    .then(pets => {
      this.setState({
        pets: pets
      })
    })
  }

  renderPets = () => {
    let petNameArray = this.state.pets.map((pet) => {
      return <h1>{pet.name}</h1>
    })
    return petNameArray
  }

  componentDidMount(){
    this.fetchPets()
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <p>
            Hello, welcome to PawsUp! These are our pets!
          </p>
          <div>{this.renderPets()}</div>
          <img src="https://i.redd.it/vuhwjpc8hb821.jpg"/>
        </header>
      </div>
    );
  }
}

export default App;
